float AA=2.;
vec3 fractal(vec2 crd){
    vec2 c=((2.*crd-iResolution.xy)/iResolution.y)/35.;
   	vec2 ms=2.*iMouse.xy/iResolution.xy-1.;
    c+=ms;
   	vec2 z=c;
    float dz=dot(z,z);
    float i;
    float m=0.;
    for(i=0.;i<20.;i++){
		z=abs(z)/dz-vec2(.9,.3);
        m=max(m,dz);
        dz=dot(z,z);
	}
    for(i=0.;i<64.;i++){
    	z=vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y)-vec2(.9,.3);
        if(dot(z,z)>4.){
        	break;
        }
    }
    float ic=5.*i/64.;
    float it=clamp(sqrt(m)/(20.+15.*sin(iTime)),0.,8.);
    vec3 col=cos(it-ic+vec3(1.,2.,3.));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 col=vec3(0.);
    for(float i=0.;i<AA;i++){
    	for(float j=0.;j<AA;j++){
    		col+=fractal(fragCoord+(vec2(i,j)/AA));
    	}
    }
    col/=AA*AA;
    fragColor = vec4(col,1.0);
}
