float leaf(float angle,float numbeleaf)
{
    float radius = abs(sin(angle+10.+iTime)*sin(angle*numbeleaf+iTime))*2.;
    return radius;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec3 red = vec3(1.,0.,0.);
vec3 blue = vec3(0.,0.,1.);
vec3 green = vec3(0,1.,0.);
vec3 yellow = vec3(1.,1.,0.);
vec3 black = vec3(0.,0.,0.);
vec3 gray = vec3(.5,.5,.3);


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos= vec2(.5*ratio,.5);
    float dist = distance(shapePos,uv)*2.;

    uv -=shapePos;
    float angle= atan(uv.y,uv.x);
    float radius = cos(.2*angle);


    float value = 1.-step(leaf(angle,10.),dist);

    vec3 val = mix(yellow,sin(iTime)+green,value);

    fragColor = vec4(val,uv);

}
