// Animated Greyscale Crystal
// my step by step tutorial
// Inspired by https://www.youtube.com/watch?v=l-07BXzNdPw

// create random numbers for x and y
vec2 N22(vec2 p) {
    vec3 a = fract(p.xyx*vec3(123.34, 234.34,345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y; // -1 <> 1

    // Change values here ------------------------------------------------------

    float window = 1.3;
    float t = (iTime+150.)*.1;
    float density = 500.;
    float brightness = .3; // high values for more brightness
    float contrast = 2.; // low values for higher contrast

    // -------------------------------------------------------------------------

    float minDist = 50.;
	float xFactor = (iResolution.x/iResolution.y)*window;
    float m = 0.;
    float cellIndex = 0.;
    float Greyscale = 1.;

    for(float i=0.; i<density; i++) {
        vec2 n = N22(vec2(i));
        vec2 p = sin(n*t);
		p.x *= xFactor;
		p.y *= window;

        float d = length(uv-p);

        if(d<minDist) {
	    	minDist = d;
            cellIndex = i;
            Greyscale = brightness+(cellIndex/(density*contrast));
		}
    }

    vec3 col = vec3(Greyscale);	

    fragColor = vec4(col, 1.0);
}
