vec3 black = vec3(0.0,0.0,0.0);
vec3 maroon = vec3(0.7,0.0,0.0);
vec3 blue = vec3(0.5,0.5,.5);
vec3 yellow = vec3(0.9,0.9,0.0);
vec3 white = vec3(1.0,1.0,1.0);


float spikey(vec2 uv, vec2 pos, float r)
{   
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float speed = 0.5;

    float angle = (atan(uv.y, uv.x) - iTime * speed);

    float radius = sin(angle * 74.0) * sin(angle * 40.0) * sin(angle * 8.0);

    value = step(radius , dist);

    return value;
}

float flower(vec2 uv, vec2 pos, float r)
{   
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float speed = 2.0;

    float angle = (atan(uv.y, uv.x) + iTime * speed);

    float radius = abs(cos(angle * 10.0));

    value = step(radius , dist);

    return value;
}

float gear(vec2 uv, vec2 pos, float r)
{   
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float speed = 2.0;

    float angle = (atan(uv.y, uv.x) + iTime * speed);

    float radius = smoothstep(-0.5, 1.0, cos(angle * 8.0)) * 0.2 + 0.5;

    value = step(radius , dist);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = 0.5 + 0.5*fract(iTime+uv.xyx+vec3(0,2,4));

    uv.x *= iResolution.x/iResolution.y;

    float mSpikey = spikey(uv, vec2(sin(0.9 * iTime) / 1.5 + 0.9, 0.2), 5.0);
    float mFlower = flower(uv, vec2(sin(2.0 * iTime) / 2.5 + 0.7,
                                    cos(2.0 * iTime) / 2.5 + 0.5), 5.0);
    float mGear = gear(uv, vec2(sin(0.5 * iTime) / 1.5 + 1.0,
                                    cos(2.0 * iTime) / 2.5 + 0.5), 5.0);

    vec3 spiCol = mix( col, black, mSpikey);
    vec3 floCol = mix(col2, black, mFlower);
    vec3 geCol = mix(col, black, mGear);

    vec3 image = mix(spiCol, blue, spiCol);
    image = mix(floCol, vec3(mFlower), image);
    image = mix(geCol, vec3(mGear), image);

    // Output to screen
    fragColor = vec4(image,1.0);
}
