// based on...
// https://www.shadertoy.com/view/lssfW2

float nsin(float x)
{
    return .5+.5*sin(x);
}

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}  //https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9

highp float rand(vec2 co)  //from stackoverflow
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 curve_p = vec2(0,0);
    float dtemp = 1000.0;
    float d = 10001.0;
    //float d2 = 10002.0;
    //float d3 = 10003.0;
    float di = 0.;
    //float threshold = 0.005;//0000006;
    float r = 0.;
    float g = 0.;
    float b = 0.;
    //float black_threshold=0.5;// -.5*nsin(iTime*.6);

    for(int i = 0;i<3;i++)
    {
        di = float(i)*2.3;
        curve_p.x = -.1*sin(.3*iTime)+.04*float(di)*sin(float(di)+.4*iTime) +.03*float(di)*sin(4.0*di+.75*iTime) + .03*sin(iTime*.1)*float(di)*sin(di*di*.005+.8*iTime);
        curve_p.y = -.1*cos(.3*iTime)+.04*float(di)*cos(float(di)+.4*iTime) +.03*float(di)*cos(4.0*di+.75*iTime) + .03*sin(iTime*.1)*float(di)*cos(di*di*.005+.8*iTime);
        dtemp = pow(length(.9*float(i)*p-curve_p),1.25);


        d= dot(normalize(p),normalize(curve_p))+3.*sin(.5*iTime+.1*d*d*d)+2.*cos(dtemp*dtemp+.1*iTime);
        //if(d<black_threshold) d=020.5;   
        r = map(8.*rand(vec2(float(i),float(i))), 0.,2000.,0.,1.); // +nsin(iTime*.8);
        g = map(g+.3*d*dtemp, 0.,2000.,0.,1.);
        b = map(.0004*dtemp*dtemp-d - 1.5*mod(log(.8*dtemp*dtemp-3.*d), .8), 0.,2000.,0.,1.); //+.001*rand(vec2(34.3,floor(dtemp*32.))); //b + .05*dtemp-.05*d;  
    }
    vec3 col = vec3(r, g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4(col,1.0);
}
