float circle(vec2 _center,vec2 uv,float _radius){
    vec2 d = (uv - _center);
    return 1. - smoothstep(_radius - _radius * .01,
                          _radius + _radius * .01,dot(d,d) * 2.);
}

vec2 proj2Screen(vec2 pos){
    pos.x = pos.x * iResolution.x / iResolution.y;
    return pos;
}

vec4 tile(vec2 uv,float zoom,float wComph){
    uv = uv * vec2(zoom) * vec2(wComph,1);
    vec4 resUv = vec4(fract(uv),0.,0.);
    resUv.zw = 2. * step(1.,mod(uv , 2.)) - 1.;
    return resUv;
}

vec2 animate(vec4 uv,float frequent){
    float speed = 1. / frequent;
    float iTimeOdd = step( 1. ,mod(iTime / frequent,2.));
    vec2 resUv = uv.xy + uv.wz * vec2(iTimeOdd,1. - iTimeOdd) * iTime * speed;

    return fract(resUv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float wComph = iResolution.x / iResolution.y;

    vec4 tileduv = tile(uv,6.1,wComph);

    uv = animate(tileduv,0.5);


    vec2 center = vec2(.5,.5);

    vec2 colvec = (tileduv.zw + 1.) * .5;

    vec3 col = vec3(colvec,1.) * circle(center,uv,.15);

    fragColor = vec4(col,1.0);
}
