// based on...
// https://www.shadertoy.com/view/lssfW2
highp float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

highp float fsin(float x)
{
    return .5 +.5*sin(x);
}
highp float fcos(float x)
{
    return .5 +.5*cos(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec2 curve_p = vec2(0,0);
    float d = 10000.0;
    float di = 0.;
    for(int i = 0;i<1;i++)
    {
        di = float(i)*.15;
        curve_p.x = .3*sin(.01*iTime)+.5*float(di)*sin(float(di)+.2*iTime) +.51*sin(5.0*di+iTime) + .2*sin(iTime*.1)*cos(12.0*di+.8*iTime);
        curve_p.y = .3*cos(.01*iTime)+.5*float(di)*cos(float(di)+.2*iTime) +.51*cos(5.0*di+iTime) + .2*sin(iTime*.1)*sin(12.0*di+.8*iTime);
        d = min(d, length(p-curve_p)+8.*rand(vec2(di,di)));

    }


    float r = .0025*d*(.5+.5*sin(.2*iTime));
    float g = .1*sin(.01*d);
    float b = .25*abs(.015+.013*(.5+.05*cos(iTime*.4)));
    if (d<.1 +.05*fsin(iTime*.2 +.778))
    {
        b=.1*pow(b+.2*d,.25);
    } else if (d<.25)
    {
        b = .1*pow(b,.5)*(.5+.5*dot(curve_p,p));
    } else
    {
        b = .02*(p.x),3.*(p.y + .14*fsin(iTime +1.*p.x));

    }
    if (d>.05 && d < 1. +.06*fsin(iTime*.3))
    {
        r = 20.8*pow(r,2.5+fcos(iTime*.2)) *(.5+.05*dot(curve_p,p));
    } else
    {
        r = .05*(sin(iTime*.5)*p.x+cos(iTime*.5)*p.y) *.5;

    }
    if (d < 1.5)
    {
        g = .005*(cos(.05*iTime-1.57)*p.x*2.+sin(.05*iTime*-1.57)*p.y*2.); 
        g = g*dot(p,curve_p + vec2(-.25,-.25));
        r = r*20.0*dot(p,curve_p + vec2(.25,.25));
        b = b*.005 + .01*dot(p,curve_p);
    }  else
    {
        g = .005*mod(200.+3.2*(fsin(.0333*iTime)*p.x+fcos(.0333*iTime)*p.y), .75*(.85*fcos(.0333*iTime+.02*(p.y-1.))+.5*fsin(.0333*iTime +.02*(p.x-1.)))+(2.+.05*dot(curve_p,p)));
    }
    vec3 col = vec3(r,g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4( pow(col,vec3(1./2.2)),1.0);
}
