float circle(vec2 center, vec2 offset, float radius, vec2 position, float blur){
	center += offset;
    float distance = length(center - position);

    return smoothstep(radius,radius - blur, distance);
    //if(distance <= radius)
    //   return 1.0;
   	//return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set backGround Color
    vec3 bgc = vec3(1.0, .0, 1.0);

    vec2 uv = fragCoord.xy / iResolution.xy;
    //transform origin to center of screen
    uv -= 0.5;
    //uv *= 2.0;
    //scale x
    uv.x *= iResolution.x / iResolution.y;
    vec2 center = vec2(0.0);
    float radius = 0.4;
    //face
    float col = circle(center, vec2(0.0), radius, uv, 0.05);

    //eyes
    col -= circle(center, vec2(-0.18, 0.09), 0.1, uv, 0.05);
    col -= circle(center, vec2(0.18, 0.09), 0.1, uv, 0.05);

    //mouth
    float mouth = circle(center, vec2(.0), 0.3, uv, 0.05);
    mouth -= circle(center, vec2(.0, 0.03), 0.31, uv, 0.05);

    col -= max(mouth, 0.0);

    fragColor = vec4(bgc + vec3(col), 1.);
}
