#define TWO_PI 6.2831


// Beats per second
const float BPS = BPM / 60.0;

float _t = 0.0;

float fade(float t, float p) {
	return exp(-3.0 * fract((_t - t)/SONG_LENGTH_BEATS)*(SONG_LENGTH_BEATS/p));   
}

float freq(float hz) {
	return sin(TWO_PI * hz / BPS * _t * PITCH);
}

float pling(float noteHz, float pos, float len, float vol) {
	return freq(noteHz) * fade(pos, len) * vol;
}

// constant-volume pling
float cpling(float noteHz, float pos, float len) {
	return pling(noteHz, pos, len, 0.5);
}


// SONG START

float getBassline1(float bar) {
    float offset = bar * BEATS_IN_BAR;
    float song = 0.0;
    song += cpling(G4, offset + 0.0, QUARTER_NOTE);
    song += cpling(B4, offset + 1.0, QUARTER_NOTE);
    song += cpling(C4, offset + 2.0, QUARTER_NOTE);
    song += cpling(B4, offset + 3.0, QUARTER_NOTE);
	return song;
}

float getBassline2(float bar) {
    float offset = bar * BEATS_IN_BAR;
    float song = 0.0;
    song += cpling(A3, offset + 0.0, QUARTER_NOTE);
    song += cpling(G4, offset + 1.0, QUARTER_NOTE);
    song += cpling(E4, offset + 2.0, QUARTER_NOTE);
    song += cpling(G4, offset + 3.0, QUARTER_NOTE);
	return song;
}


float getBassline3(float bar) {
    float offset = bar * BEATS_IN_BAR;
    float song = 0.0;
    song += cpling(F3, offset + 0.0, QUARTER_NOTE);
    song += cpling(F4, offset + 1.0, QUARTER_NOTE);
    song += cpling(C4, offset + 2.0, QUARTER_NOTE);
    song += cpling(F4, offset + 3.0, QUARTER_NOTE);
	return song;
}

float getBassline4(float bar) {
    float offset = bar * BEATS_IN_BAR;
