    song += cpling(C5, offset + 1.0, QUARTER_NOTE);
    song += cpling(C4, offset + 2.0, QUARTER_NOTE);
    song += cpling(C5, offset + 3.0, QUARTER_NOTE);
	return song;
}

float getMelody(float bar) {
    float offset = bar * BEATS_IN_BAR;

    float song = 0.0;
    song += cpling(B5, offset + 0.0, EIGHTH_NOTE);
    song += cpling(C6, offset + 0.5, EIGHTH_NOTE);
