    return song;
}

float getSong() {
    float song = 0.0f;
    song += getMelody(0.0);

    // Intro
	song += getBassline1(0.0);
    song += getBassline1(1.0);

    // Melody start
    song += getBassline2(2.0);
    song += getBassline2(3.0);

    song += getBassline3(4.0);
    song += getBassline4(5.0);

    song += getBassline3(6.0);
    song += getBassline4(7.0);

    return song;
}


// SONG END

vec2 postProcess(float freqs) {
    return vec2(freqs * 0.3);
}

vec2 mainSound( in int samp,float time) {
    //time = float(int(4096.0 * time))/4096.0;
    _t = mod(time * BPS, SONG_LENGTH_BEATS);
    return postProcess(getSong());
}
