/*
smooth coloring  : https://iquilezles.org/articles/msetsmooth
*/

// 225c (coyote)
void mainImage(out vec4 o, vec2 f)
{
    for(
        vec2 u = 4.*f/iResolution.xy - 2., i = u-u;
        i.x < 1e2 && dot(u,u) < 4e3;
        o.xy = i++ - log2(log2(dot(u,u)))
    )
        u = mat2(u,-u.y,u)*u + vec2(cos(iTime/4.),sin(iTime/8.));
    o = sin( o.x + vec4(0,1,2,0) )/4. + vec4(.5,.3,.2,0);
}

