#define PI 3.1415926538
#define thickness 0.01

float cubex = -0.8;
float cubey = -0.8;

bool looseEquals( float value1, float value2 )
{
    return value1-thickness < value2 && value1+thickness > value2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y * PI;

    uv.x += cos(iTime+uv.y*uv.x-iMouse.x/50.0);
    uv.y += cos(iTime+uv.x*uv.y-iMouse.y/30.0);

    if (  ( looseEquals(uv.x, cubex) || looseEquals(uv.x, -cubex) ) && uv.y > cubey && uv.y < -cubey*1.5
       || ( looseEquals(uv.y, cubey) || looseEquals(uv.y, -cubey) ) && uv.x > cubex*1.5 && uv.x < -cubex) {
    	vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    	fragColor = vec4(col,1.0);
    }

    if ( ( uv.x < cubex || uv.x > -cubex ) || ( uv.y < cubey || uv.y > -cubey ) ) {
        uv.y *= 0.9;
        uv.x += 0.7;

        vec3 col = 0.5 + 0.5*cos(iTime+cos((iMouse.x+iMouse.y)/50.0)+uv.xyx+vec3(0,2,4));

    	fragColor = vec4(col/4.0,1.0);
    }
}
