void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;

    vec2 q = p-vec2(0.5,0.5);

    q.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(1.0,1.0,1.0);

    q = abs(q); //symmetry
    q = (q + vec2(q.y, -q.x))*sqrt(0.5); // rotate 45 degrees


    float r = 0.3 + 0.1*cos(atan(q.y,q.x)*iTime*20.0);

    col *= smoothstep(r, r+0.01, length(q)); 

    fragColor = vec4(sin(col.x), sin(col.y-r), sin(col.z-r) ,1.0);
}
