const float PI = 3.141592;

const float BLINK_SPEED  = 8.0;  //点滅速度
const float BLINK_RADIUS = 0.001; //点滅半径倍率

vec2 Star1;

void SetStar(){
    Star1.x = 320.0;
    Star1.y = 180.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    SetStar();//初期化

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 backcol = vec3(0,0,0); //背景色
    vec3 col 	 = vec3(1,1,1); //点滅色

    float Length = distance(Star1.xy / iResolution.xy,uv.xy);//点滅中心からの距離計算

    //色計算
    col = (1.0 / Length * col) * BLINK_RADIUS * ((sin(mod(iTime * BLINK_SPEED,PI)) + 1.0) / 2.0);

    // Output to screen
    fragColor = vec4(backcol + col,1.0);
}
