#define MAX_STEPS 100
#define MIN_SURFACE_DIST 0.01
#define MAX_DIST 100.0

float get_dist(vec3 p)
{
    float t = 11.0;
    vec3 s = vec3(0, 1, 6);
    float r = (sin(p.y * 2.0 + t * 5.0) + 0.5)* 2.0 * 0.6 + 
        (sin(p.x * 2.0 + t * 5.0) + 0.5)* 2.0 * 0.6 + 
        (sin(p.z * 0.5 + iTime * 5.0) + 0.5)* 2.0 * 0.1;
	float sd = length(p - s.xyz) - r;

    float pd = p.y;
    return min(sd, pd);
}

vec3 get_normal(vec3 p)
{
	vec2 e = vec2(0.01, 0);
    float d = get_dist(p);
    vec3 n = vec3(
        d-get_dist(p-e.xyy),
        d-get_dist(p-e.yxy),
        d-get_dist(p-e.yyx)
    );
    return normalize(n);
}

float march(vec3 ro, vec3 rd)
{
    float total_dist = 0.0;
    for(int i = 0; i < MAX_STEPS; i++)
    {
    	vec3 p = ro + rd * total_dist;
        float dist = get_dist(p);
        total_dist += dist;
        if(dist < MIN_SURFACE_DIST || total_dist > MAX_DIST)
        {	break;	}
    }
    return total_dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //	start it off
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec3 col = vec3(0);

    // light
    vec3 light_center = vec3(0, 10, 6);
    vec3 light_offset = vec3(
    	(sin(iTime) - 0.5) * 2.0,
        0.0,
        (cos(iTime) - 0.5) * 2.0);
    vec3 light = light_center + light_offset * 5.0;

    //	fire a ray for each pixel
    vec3 ro = vec3(0, 1, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    //	march the ray
	float total_dist = march(ro, rd);
    vec3 hit_pos = ro + rd * total_dist;

    //	lighting
    vec3 to_cam = normalize(ro - hit_pos);
    vec3 to_light = normalize(light - hit_pos);
    vec3 normal = get_normal(hit_pos);

    float b = max(dot(normal, to_light), 0.0);


    col = vec3(b);

    fragColor = vec4(col,1.0);
}
