// v1.1

#define PI 3.1415926

mat2 rotate(float rad) {
	return mat2(cos(rad),-sin(rad),
		    sin(rad),cos(rad));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord / iResolution.x;
	vec2 grad = fragCoord / iResolution.x;
	uv *= rotate(-PI/40.);
	uv *= 15.;
	uv.y += iTime/3.;

	float mult = 1.;
	if (mod(floor(uv.x)+floor(uv.y),2.) == 0.) { mult = -1.; }

	vec3 col = .95/vec3(15.5-sin(fract(uv.y)*PI*mult)*15.);
	fragColor = vec4(col,1.);

	uv.y -= iTime/3.;
	uv.x += iTime/3.;

	mult = 1.;
	if (mod(floor(uv.x)+floor(uv.y),2.) == 0.) { mult = -1.; }

	col = .95/vec3(15.5-sin(fract(uv.x)*PI*mult)*15.);
	fragColor+= vec4(col,1.);

	fragColor *= vec4(.9,.3,.8*(1.-grad.x)+.1,1.);
	fragColor += vec4(.3,.1,.3*(1.-grad.x),1.);
}
