const float squareSideLength = 0.01; 

const vec4 colorBlack = vec4(0);
const vec4 colorWhite = vec4(1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 muv = iMouse.xy / iResolution.y; 

    fragColor = colorBlack;

    if (muv == vec2(0)){
        muv = .5+.3*vec2(cos(iTime),sin(iTime));
    }

    if (abs(uv.x - muv.x) < squareSideLength && 
        abs(uv.y - muv.y) < squareSideLength){
            fragColor = colorWhite;
    }
}
