float sd_sphere(vec3 p, float r) 
{
    return length(p) - r;
}

float map(vec3 pos)
{
    // create floor
    float d = pos.y - -0.5;

    vec3 p = pos;

    p.xz += vec2(sin(iTime), cos(iTime));

    d = min(d, sd_sphere(p, 0.5));

    d = min(d, sd_sphere(p - vec3(0.3, 0.3, 0), 0.25));
    d = min(d, sd_sphere(p - vec3(-0.3, 0.3, 0), 0.25));

    return d;
}

vec3 calc_normal(vec3 p)
{
    vec2 e = vec2(0.0001, 0.0);
    return normalize(vec3(map(p + e.xyy) - map(p - e.xyy),
                          map(p + e.yxy) - map(p - e.yxy),
                          map(p + e.yyx) - map(p - e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    fragCoord *= 2.0;
	vec2 p = (fragCoord - iResolution.xy ) / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, -2.0); // ray origin
    vec3 rd = normalize(vec3(p, 1.5)); // ray direction

    vec3 col = vec3(0);

    /**
     * Raymarch
     **/

    float total = 0.0;
    for (int i = 0; i < 100; ++i) {
        vec3 pos = ro + total * rd;

        float dist = map(pos);

        if (dist < 0.001) break; // to close
        total += dist;
        if (total > 20.0) { // to far
            total *= -1.0;
            break;
        }
    }

    if (total > 0.0) {
        vec3 pos = ro + total * rd;
        vec3 norm = calc_normal(pos);


        // col = vec3(norm);
        // col = vec3(norm.yyy);
        col = vec3(norm.xy, -norm.z);
    }

    fragColor = vec4(col, 1.0);
}
