#define MAX_MARCHING_STEPS 24

float distScene(vec3 position){
    position = fract(position)*2.-1.;
    float sphere = length(position) - .15;
    return sphere;
}
float trace(vec3 ro, vec3 rd){
	float depth = 0.;
    for(int i = 0;i < MAX_MARCHING_STEPS;i++){
    	vec3 current = ro + depth * rd;
        float t = distScene(current);
        depth += t*.5;
    }
    return depth;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3( iTime*.2, iTime*.2, iTime*0.1);

    float angle = iTime*.001;
    ro.xz *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec3 rd = normalize(vec3(uv,1.));
    float t = trace(ro, rd);
    float fog = 1./(1.+t*t*.1);
    fragColor = vec4((vec3(uv.x+1.5,uv.y-1.,1.)*fog),1.0);
}
