const float xSamples = 8.0;
const float ySamples = 8.0;

const float xSampleSpacing = 1.0;
const float ySampleSpacing = 1.0;

const float half_x_samples = xSamples / 2.0;
const float half_y_samples = ySamples / 2.0;

const float min_sample_x = -half_x_samples;
const float min_sample_y = -half_y_samples;

const float max_sample_x = half_x_samples;
const float max_sample_y = half_y_samples;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 totalSample = vec3(0.0, 0.0, 0.0);
    float samples = 0.0;
    for (float xOffset = min_sample_x; xOffset < max_sample_x; xOffset++) {
        for (float yOffset = min_sample_y; yOffset < max_sample_y; yOffset++) {
            vec2 sampleCoords = (fragCoord + vec2(xOffset * xSampleSpacing, yOffset * ySampleSpacing)) / iResolution.xy;
            vec3 sampleColor = texture(iChannel0, sampleCoords).xyz;
            totalSample += sampleColor;
            samples += 1.0;
        }
    }
    vec3 avgSample = totalSample / samples;
    fragColor = vec4(avgSample, 1.0);
}
