#define pi 3.14159265359
float truemod(float x,float y) { float v = mod(x,y); return (v==y)? 0. : v; }


// isPrime:  Created by inigo quilez - iq/2013 https://www.shadertoy.com/view/4slGRH
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Info on prime hunting: https://iquilezles.org/blog/?p=1558
float isPrime( float x )
{
	if( x==1. ) return 0.;
	if( x==2. ) return 1.;
	if( x==3. ) return 1.;
	if( x==5. ) return 1.;
	if( x==7. ) return 1.;

	if( mod(x,2.)==0. ) return 0.;
	if( mod(x,3.)==0. ) return 0.;
	if( mod(x,5.)==0. ) return 0.;

	float y = 7.;
	bool flip =   true; // (mod(iTime*4.,1.)>.5);
	float xmax = (flip) ? sqrt(x)+1. : x;
	//int max =  (flip) ? 2000:200;

	for( int i=0; i<200; i++ ) // count up to 6000
	{ 
	    // if (i>max) {  return 1.; }
		if( truemod(x,y)==0. )  return 0.;
		y += 4.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 2.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 4.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 2.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 4.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 6.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 2.; if( y>=xmax ) return 1.;
		if( truemod(x,y)==0. )  return 0.;
		y += 6.; if( y>=xmax ) return 1.;
	}

	return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;

    float t = iTime * .5;

    uv *= sin(t*.5)*.5+.5 + .1;
    uv *= 3.;

    float d = length(uv);
    float angle = atan(uv.x,uv.y)*.5/pi +.5;
    vec2 uv2 = vec2(angle,d-(angle-1.)*.01);
    uv2 *= 100.;
    float num = pow(uv2.x*.01+floor(uv2.y),2.);
    float prime = isPrime(floor(num));

    fragColor = mix(vec4(prime),vec4(fract(uv2*.01)*fract(num), prime*fract(num),1.0),smoothstep(-1.,1.,sin(t*3.)));
    //fragColor = vec4(prime);
}
