void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //fragColor = texture(iChannel1, uv);
    //return;

    vec2 v = texture(iChannel1, uv).rg;
    v = mod(v, 1.0);
    v.x = (v.x + 0.01) * 0.91;
    //v.y = (v.y + 0.35) * 0.60;
    v.x /= 2.0;
    //v.x /= 6.0;
    fragColor = texture(iChannel3, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5);

    uv.x = (sin(uv.x / (uv.y + 0.1) + iTime / 10.0) + 1.0) / 2.0;
    uv.y = (sin(uv.x / (uv.y + 0.1) + iTime / 10.0) + 1.0) / 2.0;

    vec2 col = vec2(
        (sin(uv.x * 13.0 + uv.y * 27.0) + 1.0) / 2.0,
        (sin(uv.x * 17.0 + uv.y * 23.0) + 1.0) / 2.0
    );

    fragColor = vec4(col.xy, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 t = texture(iChannel3, uv);

    if (iFrame < 8 || uv.y < 0.01 || uv.y > 0.99 || uv.x < 0.01 || uv.x > 0.99) {
        fragColor = t;
        return;
    }

    vec2 v = texture(iChannel0, uv).xy;
    v = mod(v, 1.0) - vec2(0.5);


    if (length(v) < 0.04) {
        fragColor = t;
        return;
    }


    //vec2 pos = uv + (v / 3000.0);
    vec2 pos = uv + (v / (2000.0 - (max(1.00-iTime, 0.0)*2000.0)));

    vec3 c = texture(iChannel1, pos).rgb;
    fragColor = vec4(c, 1.0);
}
