void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 video1Cor = texture(iChannel0, xy);
    vec4 video2Cor = texture(iChannel1, xy);
    if(video1Cor.g > 0.3 && video1Cor.r + video1Cor.b < 0.35) {
    	fragColor = vec4(video2Cor);
    } else {
    	fragColor = vec4(video1Cor);
    }
}
