void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(mod(uv,mainSound(1,iTime+uv.x)),1.0,1.0);
}

//por jorge flores ---jorge2017a1
vec2 mainSound( in int samp, float i )
{ float t=fract(i/52.)*52.;
    float b=fract(t/4.)<5./6.?180.*acos(-1.):108.*acos(-1.);


 	vec2 c1=vec2( sin(b*t)*exp(-3.0*fract(t/2.)),sin(b*t)*exp(-5.0*fract(--t/2.)) );
 	vec2 c2=vec2( sin(b*floor(t*c1/8.)*(t))*exp(-2.0*fract(t*3.+.5)));
    vec2 c3=clamp( vec2( sin(b*floor((t+1.8)/8.)*(t))*exp(-3.0*fract(t*1.5+.5))),.2,.8);
 	vec2 c4=vec2( sin(c3*floor(t/4.)*(t)+2.0*sin(c2*t*0.5))*exp(-2.0*fract(t*3.+.5)));
    vec2 sum=c1+c2+c3+c4;

    return sum;
}
