//por jorge2017a1--*---------nov-14-2019

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

//Torus function
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}




//Sphere function
float sdSphere( vec3 p, float s1 )
{
   vec4 s = vec4(0, s1, 9, s1);
   return  length(p-s.xyz)-s.w;   
}
//Box function
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}
//Triprism function
float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}
//Cone function
float sdCone( vec3 p, vec2 c )
{
    // c must be normalized
    float q = length(p.xy);
    return dot(c,vec2(q,p.z));
}



///--------------------------------------------


float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}




//Coordinate positioning of each shape
float GetDist(vec3 p) {	


    float planeDist = p.y;
    float dm1, dm2,dm3,dm4;
    float d;


    float bx1 = sdBox (p-vec3(0.,0.5,5.0),vec3(1.5,1.5,0.5));
    float bx2 = sdBox (p-vec3(0.,0.5,5.0),vec3(0.3,0.5,1.0));
    float sp1 = sdSphere (p-vec3(0.0,1.0,-4.0), 0.8);


    float tri1 = sdTriPrism (p-vec3(4.5,0.2,6),vec2(3.1,1.2));
    float bx3 = sdBox (p-vec3(4.5,0.2,6.0),vec3(0.5,1.5,1.5));


    float sp2 = sdSphere (p-vec3(-5.0,0.0,-4.0), 1.8);
    float bx4 = sdBox (p-vec3(-5.0,1.2,6.0),vec3(0.5,1.5,3.5));

    float dif1, dif2, dif3;


    d=1000.0;
    d=min(d,planeDist);
    dif1=differenceSDF(bx1,bx2);
    dif1=differenceSDF(dif1,sp1);

    d=min(d, dif1);

    //intersectSDF
    //unionSDF
    //differenceSDF

    dif2=differenceSDF(tri1,bx3);

    dif3=differenceSDF(sp2,bx4);



    d = min(d,dif2);
    d = min(d,dif3);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}




float GetLightv2(vec3 p) {
    vec3 lightPos1 = vec3(1, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos1.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l1 = normalize(lightPos1-p);
    vec3 n1 = GetNormal(p);

    float dif1 = clamp(dot(n1, l1), 0., 1.);
    float d1 = RayMarch(p+n1*SURF_DIST*2., l1);
    if(d1<length(lightPos1-p)) dif1 *= .1;



    vec3 lightPos2 = vec3(0, 5, -6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos2.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l2 = normalize(lightPos2-p);
    vec3 n2 = GetNormal(p);

    float dif2 = clamp(dot(n2, l2), 0., 1.);
    float d2 = RayMarch(p+n2*SURF_DIST*2., l2);
    if(d2<length(lightPos2-p)) dif2 *= .1;

    return (dif1+dif2)/2.0;
}


float GetLightv3(vec3 p) {
    vec3 lightPos1 = vec3(-1, 5, -6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos1.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l1 = normalize(lightPos1-p);
    vec3 n1 = GetNormal(p);

    float dif1 = clamp(dot(n1, l1), 0., 1.);
    float d1 = RayMarch(p+n1*SURF_DIST*2., l1);
    if(d1<length(lightPos1-p)) dif1 *= .1;



    vec3 lightPos2 = vec3(2, 5, -6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos2.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l2 = normalize(lightPos2-p);
    vec3 n2 = GetNormal(p);

    float dif2 = clamp(dot(n2, l2), 0., 1.);
    float d2 = RayMarch(p+n2*SURF_DIST*2., l2);
    if(d2<length(lightPos2-p)) dif2 *= .1;


    vec3 lightPos3 = vec3(-2.5, 5.5, -5.5);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos3.xy += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l3 = normalize(lightPos3-p);
    vec3 n3 = GetNormal(p);

    float dif3 = clamp(dot(n3, l3), 0., 1.);
    float d3 = RayMarch(p+n3*SURF_DIST*2., l3);
    if(d3<length(lightPos3-p)) dif3 *= .1;


    return (dif1+dif2+dif3)/3.0;
}



float GetLightPos(vec3 p, vec3 lpos) {

    vec3 lightPos1 = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos1.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l1 = normalize(lightPos1-p);
    vec3 n1 = GetNormal(p);

    float dif1 = clamp(dot(n1, l1), 0., 1.);
    float d1 = RayMarch(p+n1*SURF_DIST*2., l1);
    if(d1<length(lightPos1-p)) dif1 *= .1;


    vec3 lightPos2 =lpos;
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos2.xz += vec2(0.0, cos(iTime)*2.);
    vec3 l2 = normalize(lightPos2-p);
    vec3 n2 = GetNormal(p);

    float dif2 = clamp(dot(n2, l2), 0., 1.);
    float d2 = RayMarch(p+n2*SURF_DIST*2., l2);
    if(d2<length(lightPos2-p)) dif2 *= .1;

    return (dif1+dif2)/2.0;
}

