    //Color of each object
    //vec3 ro = vec3(0, 1.0, 0);

    vec3 ro = vec3(4.0+4.0*cos(iTime), 2.5, -4.0);
    vec3 rd = normalize(vec3(uv.x-.15, uv.y-.2, 1));

    //
    float d = RayMarch(ro, rd);
    //vec3 p;

    //vec3 p = (ro + rd * d )+texture(iChannel0, uv).x;
    vec3 p = (ro + rd * d );

    //float dif = GetLight(p);
    float dif2= GetLightv2(p);


    col = vec3(dif2);



    fragColor = vec4(col,1.0);
    //Background color is white
