vec4 topAndBottom(vec2 xy)
{
    float l=step(1.,xy.y);
    xy.y=l*(2.-xy.y+abs(sin(iTime)))+(1.-l)*(xy.y+abs(sin(iTime)));
    vec4 texColor=texture(iChannel0,xy);
    return texColor;
}

vec4 leftAndRight(vec2 xy)
{
    float l=step(1.,xy.x);
    xy.x=l*(2.-xy.x+abs(sin(iTime)))+(1.-l)*(xy.x+abs(sin(iTime)));
    vec4 texColor=texture(iChannel0,xy);
    return texColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 xy = fragCoord/iResolution.xy;
  xy*=2.;
  vec4 texColor=topAndBottom(xy);
  //vec4 texColor=leftAndRight(xy);
  fragColor=texColor;



}
