#define S(U,n,s) smoothstep(.9,1., cos( n* 6.3*length(U) -atan((U).y,(U).x) +s*t )) //
void mainImage(out vec4 O, vec2 U) {
    float t = iTime;
    vec2 R = iResolution.xy,
         V = (U/R-.5) * mat2(cos(.3*cos(t)+vec4(0,11,33,0)))
                      * (1.+.2*sin(2.1*t)) + .5
             +.05* cos(t/.1)+.03* cos(30.*vec2(-t,t));
    O = texture(iChannel0, V ) -.5;
    O.rg *=   mat2( cos( 6.3*t  + vec4(0,23,21,0)) );
    O += .5  + S((U+U-R)/R.y, 2.,3.);
    O.b += S(V-.5, 3.,-3.);}
