vec2 hash(vec2 uv) {
    mat2 m = mat2(2114.2,1241.6,-9818.559,-33.8);
    return fract(m*sin(m*uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = fragCoord/iResolution.y*10.;
    vec2 fuv = fract(uv);
    vec2 iuv = floor(uv);

    vec2 mp1,mp2;
    vec2 d = vec2(900.);
    for (int i=-1;i<=1;i++) {
        for (int j=-1;j<=1;j++) {
            vec2 disp = vec2(i,j);
            vec2 center = disp+hash(iuv+disp)*(cos(iTime)*.5+.5);
            vec2 r = fuv - center;
            float lr = length(r);
            if (lr < d.x) {
                d.y = d.x;
                mp2 = mp1;
                d.x = lr;
                mp1 = center;
            } else if (lr < d.y) {
                d.y = lr;
                mp2 = center;
            }
        }
    }

    vec2 edge = (mp2-mp1);
    vec2 p = fuv - 0.5*(mp1+mp2);
    vec2 orth = dot(p,edge)/dot(edge,edge)*edge;

    fragColor =vec4(vec3(length(orth)),1.);
}
