renderpass.code:
float PI = 3.1415926;
mat2 rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float progress = iMouse.x/iResolution.x;
    float spread = 0.05;

    vec2 uv = fragCoord/iResolution.xy;

	// actual calculations
	float col = smoothstep(0.5-spread,0.5+spread,uv.x);
    vec2 rotUV = (uv-vec2(0.5 - 2.*spread + 4.*spread*progress, 0.5))*rotate(progress*PI*2.) + vec2(0.5,0.5);
    float col1 = smoothstep(0.5+spread,0.5-spread,rotUV.x);
    float final =  min(col1,col);
    if(progress>0.5){
        final = 1. - min((1. - col),(1. - col1));
    }


    // debug
    //fragColor = vec4(vec3(final),1.0);


    fragColor = mix(texture(iChannel0,uv),texture(iChannel1,uv), final);

    // progress bar
    if(uv.y<0.01 && uv.x<progress) fragColor = vec4(1.,0.,0.,1.);
}
