//Step on a gradient
float GS (float G, float TS, float SS)
{
    float Pos = G*TS-SS;
    return (1.0-floor(clamp(Pos,0.,1.)))*ceil(clamp(Pos,0.,1.));
}

//Cantor set level
float CSL(float Coord)
{
	//return 1.0 - round((Coord * (1.0 - Coord))*2.25);
    return Coord;
}

//Render
void mainImage( out vec4 cs, in vec2 fragCoord )
{
    //Variables
    vec2 uv = fragCoord/iResolution.xy;
    int T = 10;
    float PL = CSL(uv.x);
    float CS = PL;


    //Loop
    for(int i=0;i < T; i++)
    {
        float CurLevel = CSL(fract(uv.x*pow(3.,float(i+1))));
        CS = mix(CS,CurLevel*PL,GS(1.0-uv.y,float(T+1),float(i+1)));
        PL *= CurLevel;
    }

    //After loop
    //CS = mix(CS,1.0,GS(1.0-uv.y,float(T),float(0.0)));

    //Output to screen
    cs = vec4(CS,CS,CS,1.0);
}
