void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv1 = 2.0 * (fragCoord - vec2(iResolution.x * 0.5, iResolution.y)) / iResolution.x;

    vec2 uv2 = vec2(
        atan(uv1.y, uv1.x) / (2.0 * acos(-1.0)) * 4.0,
        length(uv1));
   	vec2 uv = mix(uv1, uv2, clamp(sin(iTime * 0.5) * 0.5 + 0.5, 0.0, 1.0));
    vec3 col = texture(iChannel0, uv).xyz;
    fragColor = vec4(col, 1.0);
}
