void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    float checkX = pow(fragCoord.x/iResolution.x + iTime, 2.0);
    float checkY = pow(fragCoord.y/iResolution.y + iTime, 2.0);

    // Output to screen
    if (sin(checkX) * cos(checkX) - sin(checkY) * cos(checkY) < 0.1)
    	fragColor = vec4(sin(checkX+checkY),cos(checkX+checkY),sin(checkX+checkY),1);
    else
        fragColor = vec4(0,0,0,1);
}
