void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord/300.0;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 a;
    float pi=3.14159;
    float r=sqrt(pow(uv.x-0.5,2.0)+pow(uv.y-0.5,2.0));
    float o=atan((uv.y-0.5)/(uv.x-0.5));

    o=o/(2.0*pi);
    if (uv.x<0.5)
        o=(o+0.5);
    o=o-iTime;
    o=mod(o,1.0);

    a.r=r*2.0/sqrt(2.0);
    //if (a.r>=0.999)//test
    //    a.r=0.0;
    a.g=o;
    a.b=1.0-a.r;

    fragColor = vec4(a,1.0);
}
