void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    float f = pow((1.-length(uv)), 1.5);
    uv.x *= 25.;
    uv.y *= 0.7;

    vec2 d = vec2(
        sin(iTime+uv.y*10.+uv.x*.3+2.*cos(iTime+uv.x*2.)),
        sin(iTime-uv.x-uv.y*30.-3.*sin(iTime-uv.y*30.))
        );

    uv += d*0.005;

    float a = sin(uv.y + iTime + cos(abs(uv.x) + uv.y*10.))*0.5+0.5;
    float b = cos(uv.y + iTime*5. + tan(abs(uv.x) + uv.y*10.));
    float c = cos(uv.y + iTime + sin(abs(uv.x) - uv.y*10.))*0.5+0.5;

    vec3 x = vec3(1., 0., 0.) * a;
    vec3 y = vec3(0., 0., 1.) * c;
    vec3 z = vec3(0., 1., 0.) * b;

    vec3 r = mix(x+y, vec3(0., 1., 0.), b);

    fragColor = vec4(r*f, 1.0);
}
