void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv = uv*cos(iTime)*4.;

    float a = uv.y + sin(sin(uv.x*cos(iTime)));

    uv.y = atan(a)*16.+10.;

    float d = floor(mod(uv.y*2.,-a*6.));

    vec3 col = vec3(d/2.,-a,fract(a*4.));

    fragColor = vec4(col,1.);
}
