// 278c FabriceNeyret version
void mainImage(out vec4 o, vec2 f)
{
    vec2 R = iResolution.xy, v = 32.*f/R, u;
    o = texture(iChannel1, ceil(v)/32. );
    float n=16., c = o.x*.6 + o.y * 1.18 + o.z * .22;
    u = floor( n*vec2( mod(c, n), c ) ) /n,
    v = fract(v) /n; R = step(v,vec2(3,2)/R);
    o = 3.* sin(vec4( 0, .5, 1.04, 0 )+c+c) * texture(iChannel0, u+v ).x
      + .5* max(R.x,R.y);
}
