renderpass.code:
vec2 line (vec2 a, vec2 b, vec2 p)
{
    vec2 pa = p-a, ba = b-a;
    float h = min(1., max(0., dot(pa, ba)/dot(ba, ba)));

    return vec2(length(pa - ba * h), h);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	uv *= 3.;
    float time = iTime * 4.;
    float sp = 2.5;

    float a = 0.1 + floor(time) * sp;
    vec2 p1 = vec2(sin(a), cos(a));
    vec2 p2 = vec2(sin(a + sp), cos(a + sp));
    vec2 p3 = vec2(sin(a + sp * 2.), cos(a + sp * 2.));

    vec2 l1 = line(p1, p2, uv);
    vec2 l2 = line(p2, p3, uv);

    l1.x = smoothstep(0.04, 0.01, l1.x);
    l2.x = smoothstep(0.04, 0.01, l2.x);
    float prog1 = pow(fract(time), 0.8);
    float prog2 = pow(fract(time), 2.);

    float al = max(smoothstep(prog2, prog1, l1.y) * l1.x, 
                   smoothstep(prog1, prog2, l2.y) * l2.x);

    vec3 col = vec3(0);
    col += al;

    fragColor = vec4(col,1.0);
}
