vec4 dot(vec2 texCoord)
{
	vec2 pos = iMouse.xy-texCoord.xy;
	vec2 radius = vec2(60,60);
    float result = dot(pos.xy/radius.xy,pos.xy/radius.xy);
    return vec4(result*abs(sin(iTime/2.0)),result,result,1);
}
void mainImage( out vec4 FragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy/iResolution.xy;
    FragColor = dot(fragCoord)/vec4(0.2,1,1,1);
}
