void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    for (float i = -10.; i <= 10.; i++) {
    	Q += A(U+2.*vec2(+i,+i))*exp(-.01*i*i);
    	Q += A(U+2.*vec2(-i,+i))*exp(-.01*i*i);

    	Q += A(U+vec2(0,+i))*exp(-.01*i*i);
    	Q += A(U+vec2(+i,0))*exp(-.01*i*i);
    }
    Q /= 80.;
    float xx = exp(-3.*dot(U-0.5*R,U-0.5*R)/R.y/R.y);
    vec4 Y = mix(A(U),B(U),xx);
    Q = mix(Y,Q,.5-.4*xx);
}

vec4 T (vec2 U) {
	return A(U-A(U).xy);
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    float d = 0.;
    for (int x = -2; x <= 2; x++) {
        for (int y = -2; y <= 2; y++) {
        	vec2 r = vec2(x,y);
            vec4 n = T(U+r);
            d += dot(r,n.xy);
        }
    }
    d /= 25.;
    Q.z = mix(Q.z,d,.3);

    //Boundary Conditions
    if (iFrame < 1) Q = vec4(sin(U.x)*cos(U.y));
    if (iMouse.z>0.)Q = mix(Q,vec4(0),smoothstep(26.,24.,length(U-iMouse.xy)));

}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
mat2 ro (float a ) {
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

vec4 T (vec2 U) {
	return A(U-A(U).xy);
}
void mainImage( out vec4 Q, in vec2 U )
{
    vec2 p = 0.5*R;
    U -= p;
    float rrr = length(U)/R.x;
    U *= (1.-.005+.003*rrr)*ro((.008-0.01*rrr));
    U += p;
    Q = A(U);
    mat2 mm = ro(6.2*Q.z);
    vec2 g = vec2(0);
    for (int x = -3; x <= 3; x++) {
        for (int y = -3; y <= 3; y++) {
        	vec2 r = vec2(x,y);
            vec4 n = T(U+r);
            r *= mm;
            if (length(r)>0.)
            g += 3.*r*(n.z-Q.z)/length(r);
        }
    }
    g /= 81.;
    Q.xy = mix(Q.xy,-g,.1);
    if (length(Q.xy)>0.) Q.xy = mix(Q.xy,normalize(Q.xy),.1);
}

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.z-w.z,n.z-s.z,1));
    vec4 a = A(U);
    Q = 0.5+0.5*a.zzzz;
    vec3 re = reflect(no,normalize(vec3((.1*(U-0.5*R)/R.y),1)));
    Q *= .9+0.003*exp(11.*texture(iChannel1,re)*vec4(.7,.95,.98,1));
	Q *= .6+0.5*no.x;
}
