    uv = uv*4.-2.;

	vec3 fond = texture(iChannel0,uv).xyz;
    // Time varying pixel color


    float d = 0.;

    //No moving
    //d = length(abs(uv)-.3);

    //Moving
    d= length( abs(uv)-abs(sin(iTime)+cos(iTime)) );


    //Color
    vec3 col = vec3(0.,0.7,0.1);

    col += vec3(max(fract(d*5.),d*abs(cos(iTime*3.)*3.)));



