// Here I am trying to find intuition behind known 
// formulas by giving all variables human-readable names.

/*
In my imaginary world any object is drawn in following steps:
	coordinates
	geometry
	graphics
	coloring

Each step has it's math and tweaks. Some tweaks are optional and some do actual job. 
And that's why GLSL is hard for me: not all tweaks are optional.

All objects are added to canvas with +=. Canvas itself is init with vec4(0).
*/

/*standard*/

#define normalization(p) (2.*p - iResolution.xy) / iResolution.y

/*support*/

#define radius 0.1
#define size vec2(1)
#define quadrantSize size / 2. 
#define center vec2(.2)
#define VIOLET vec4(0.8, 0.8, 1, 1)
#define bounceAnimation sin(iTime * .4) * .1 - .1

/*objects*/

vec4 RoundBox(vec2 p) {
    // coordinates
    p = abs(p - center); // translate to center with simmetry tweak
    p -= quadrantSize; // translate to quadrant corner
    p += radius; // tweak to fix result value without any beatiful conceptual explanation

    // geometry
    float dist = length(max(p, 0.)); // convert cs from cortesian to polar with required tweakery that does the job
    float geometry = smoothstep(0., 0.01, dist - radius);

    vec4 graphics = vec4(1. - geometry);

    vec4 coloring = graphics * VIOLET;
    return coloring;
}

/*canvas*/

void mainImage(out vec4 canvas, in vec2 pixel) {
    vec2 p = normalization(pixel);
    canvas = vec4(0);
    canvas += RoundBox(p);
}
