void mainImage( out vec4 Q, in vec2 U ){
    float y=U.y/iResolution.y;
	int id=int(U.x);
    float mindist=1e10;
    for(int i=0;i<int(iResolution.y);i++){
    	float n=texelFetch(iChannel0,ivec2(id,i),0).x;
    	float dist=abs(n-y);
   		mindist=min(mindist,dist);
    }
    Q=vec4(1.-exp(-100.*mindist));
}

void mainImage( out vec4 Q, in vec2 U ){
    float e=U.x/iResolution.x*4.;
    float n=U.y/iResolution.y;
    for(int i=0;i<400;i++)n=next(e,n);
	Q=vec4(n);
}

float next(float e, float x){
    return e*x*(1.-x);
}
