float maxStrength = 0.0125;
float minStrength = 0.0050;

#define PI 3.14159265359
mat2 rotate(float angle)
{
    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}

void mainImage(out vec4 fragColour, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    //--- Strength animate ---
    maxStrength = clamp(sin(iTime/2.0),minStrength,maxStrength);
    //-----------------------

    //--- Move UVs ---
    uv.x = uv.x + sin(iTime*5.0)*maxStrength;
    uv.y = uv.y + sin(iTime*20.0)*maxStrength;

	uv = rotate(sin(iTime*2.5)*PI*maxStrength*2.0)*uv;
    //-----------------------

    //--- Background ---
    vec3 background = vec3(texture(iChannel0, uv));
    //--------------

    //--- Colour offset ---
    background.r = texture(iChannel0, vec2(uv.x+maxStrength,uv.y+maxStrength)).r;
    background.g = texture(iChannel0, vec2(uv.x-maxStrength,uv.y+maxStrength)).g;
    background.b = texture(iChannel0, vec2(uv.x-maxStrength,uv.y-maxStrength)).b;
    //-----------------------

    fragColour = vec4(background,1.0);
}
