float circle(in vec2 st, in float x, in float y){
    return distance(st,vec2(x, y)) * 5.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord/iResolution.xy;

    float c1 = circle(st, 0.1, 0.3);
    float c2 = circle(st, 0.5, 0.5);
    float c3 = circle(st, 0.8, 0.6);    

    vec3 color = vec3((c1+c2) * abs(fract(sin(c2*iTime*50.0)*1.0)));

	fragColor = vec4( color, 1.0 );
}
