// FibonacciSpiralTextured.glsl

const float TAU = 6.28318530718;   // 2* Pi

#define t iTime*0.01

void mainImage (out vec4 color, vec2 I)
{
  vec2 R = iResolution.xy; 
  vec2 P = vec2(sin(t),cos(t));
  if (iMouse.z > 0.1)
    P = iMouse.xy / R; 
  I = (I+I-R) / R.y;
  color = texture(iChannel0, 4.*P*(exp2(-length(I)) + atan(I,I.yx)/TAU) );
}
