void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;//Соединяем это в одну строку
    vec4 texColor = texture(iChannel0,xy);//Получаем от iChannel0 пиксель в координате xy
    if(texColor.g >= texColor.r + texColor.b){
        texColor = texture(iChannel1, xy);
        texColor.r = xy.x;
        texColor.b = xy.y;
        texColor *= abs(sin(iTime));
    }
    else
    {
        float color = texColor.r + texColor.g + texColor.b;
        color /= 3.0;
        texColor.r = color; texColor.g = color; texColor.b = color;
    }
    fragColor = texColor;//Присваиваем экранному пикселю этот цвет
}
