vec3 calc( in vec2 p )
{
    float x = 0.5;
	float h = 0.0;
	for( int i=0; i<9; i++ )
	{
		x = p.x*x*(1.0-x+sin(iTime+p.y*p.x)/100.);
          h += (3.*sin(p.y*(0.5-1.0)));

		x = p.y*x*(1.0-x+sin(iTime*1.2)/80.);
        h += (3.*sin(p.y*(0.5-1.0*x)));
	}
    h /= 50.0;


	vec3 col = vec3(0.0);
	if( h<0.0 )
	{
		h = abs(h);
		col =  1.0*sin( vec3(0.9,0.5,0.0) + 6.8*h );
	}

	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 col =  calc( vec2(0.8,2.9) + 2.7*(fragCoord) / iResolution.x );
	fragColor = vec4( col, 1.0 );
}
