void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float aa = 3. / iResolution.y;
    float t = iTime * .64;
    uv *= 17.;
    vec3 col = vec3(0.);

    // camera
    uv -= (sin(t+sin(t*1.1))*2.+cos(t*1.3+sin(t*.7)))*1.4;
    uv.x -= sin(t*.3)*.3;
    uv.y -= cos(t*.4)*.3+.4;

    // grid and ball
    float size = .18;   
    float d = length(uv*cos(uv.y-3.2*fract(t+cos(t*1.1))));
    col += smoothstep(size+aa, size, d);
    d = length(uv*cos(uv.x-3.2*fract(t+sin(t))));
    col += smoothstep(size+aa, size, d);

    // bigger ball
    d = length(uv);
    col += smoothstep(size*2.+aa, size*2., d);

    // gradient
    col = clamp(col, 0., 1.);
    col *= smoothstep(4., .0, length(uv))+.7;
    col *= smoothstep(12., .0, length(vec2(uv.x+2.5*sin(t*4.), uv.y+2.5*cos(t*2.7))))+.5;
    col *= smoothstep(17., .0, length(uv))+.4;

    // color
    col *= vec3(1., .3, 0.2);

    // glow
    //d = length(uv);
    //float m = .015/d*40.1;
    //m *= smoothstep(1., .2, d);
    //vec3 glow = m *.7 * vec3(1.);
    //col += clamp(glow*.5, 0., 1.)*.06;

    fragColor = vec4(col,1.0);
}
